// These declarations enable importing image files in a TypeScript file
// - https://stackoverflow.com/a/51163365

declare module '*.png' {
    const value: string;
    export = value;
}

declare module '*.jpg' {
    const value: string;
    export = value;
}

declare module '*.jpeg' {
    const value: string;
    export = value;
}

declare module '*.gif' {
    const value: string;
    export = value;
}

declare module '*.webp' {
    const value: string;
    export = value;
}

declare module '*.bmp' {
    const value: string;
    export = value;
}

declare module '*.svg' {
    const value: string;
    export = value;
}

declare module '*.ico' {
    const value: string;
    export = value;
}

declare module '*.apng' {
    const value: string;
    export = value;
}
